import { initialState } from '../store';
import itemsTypes from './items-types';

export default itemsReducer = (items = initialState.items, { type, payload }) => {
  switch (type) {
    case itemsTypes.ADD:
      return [...items, payload];

    case itemsTypes.REMOVE:
      return items.filter((item) => item !== payload);

    default:
      return items;
  }
};
