import { itemsTypes } from './items-types';
import { v4 as uuidv4 } from 'uuid';

export const ADD = (name, number) => ({
  type: itemsTypes.ADD,
  payload: { name, number },
});

export const REMOVE = (item) => ({
  type: itemsTypes.REMOVE,
  payload: item,
});

export const REMOVE_ALL = () => ({
  type: itemsTypes.REMOVE_ALL,
  payload: [],
});
