import styled from "styled-components";

export const StyledApp = styled.section`
  background-color: #ffffff;
  border: 1px solid #c15ae0;
  border-radius: 1rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 1rem 1.5rem;

  width: 100%;

  box-shadow: 7px 7px 15px -8px rgba(0, 0, 0, 0.56);
`;

export const StyledBanner = styled.p`
  display: flex;
  align-items: center;
  justify-content: center;

  width: 100%;

  padding: 1rem 1.5rem;
  color: #fafafa;
  text-transform: uppercase;
  font-weight: 600;
  font-size: 1.5rem;

  background-color: #d6d6d6;
`;
